  const [xIsNext, setXIsNext] = useState(true);
  const [start, setStart] = useState(true);
  const winner = calculateBigWinner(bigboard);
  const [playBoard, setPlayBoard] = useState(0);
  const [boardchose, setBoardChose] = useState(false);
  const [isOverlayVisible1, setOverlayVisible1] = useState(false);
  const [isOverlayVisible2, setOverlayVisible2] = useState(true);
  const [isOverlayVisible3, setOverlayVisible3] = useState(true);
  const [isOverlayVisible4, setOverlayVisible4] = useState(false);
  const [isOverlayVisible5, setOverlayVisible5] = useState(false);
  const [isOverlayVisible6, setOverlayVisible6] = useState(false);
  const [isOverlayVisible7, setOverlayVisible7] = useState(false);
  const [isOverlayVisible8, setOverlayVisible8] = useState(false);

  const handleClick = (index, i) => {
    // Log the values of i and index
    console.log('i:', i, 'index:', index);
    if(isOverlayVisible1) return;

    if (winner || board[index][i] != null) return; //if there is a winner or square is already chosen

    board[index][i] = xIsNext ? 'X' : 'O';
    console.log("Next: ", xIsNext ? 'X' : 'O');
    setXIsNext(!xIsNext);
  };

