    </button>
  );

  const renderBigSquare = (i) => (
    <div className={`board ${isOverlayVisible1 ? 'overlay' : ''} ${isOverlayVisible2 ? 'overlay' : ''} ${isOverlayVisible3 ? 'overlay' : ''} ${isOverlayVisible4 ? 'overlay' : ''} 
    ${isOverlayVisible5 ? 'overlay' : ''} ${isOverlayVisible6 ? 'overlay' : ''} ${isOverlayVisible7 ? 'overlay' : ''} ${isOverlayVisible8 ? 'overlay' : ''}`}>
      {/* {renderSmallBoard()} */}
      {Array(3)
        .fill(null)
        .map((_, row) => (
          <div key={row} className="board-row">
            {Array(3)
              .fill(null)
              .map((_, col) => renderSquare(i, row * 3 + col))}
          </div>
        ))}
      </div>
  );

  const renderSquare = (index, i) => (
    <button className="square" onClick={() => handleClick(index, i)}>
      {board[index][i]}
    </button>
  );

  const handleReset = () => {
    // setBoard(Array(9).fill(null));
    //setBoard(Array(9).fill(Array(9).fill(null)));
    setBoard(Array(9).fill(null).map(() => Array(9).fill(null)));
    setXIsNext(true);
    setStart(true);
  };

  const renderBigBoard = () => (
    <div className="game">
      <div className="game-bigboard">
        <div className="status">{status}</div>
        <div> 
          {Array(3) 
            .fill(null)
            .map((_, row) => (
              <div key={row} className="board-row">
                {Array(3)
                  .fill(null)
                  .map((_, col) => renderBigSquare(row * 3 + col))}
              </div>
            ))}
            <button className="replay-button" onClick={handleReset}>
              Reset Board
            </button>
        </div>
      </div>
    </div>
  );

  const chooseBoard = () => {
    // if button pressed then ... 
    setBoardChose(true);
  };

  const startGame = () =>{   
     if(start){
      console.log("Start", status);
      status = "Choose a Board";
      console.log("Start", status);
      // while(!boardchose){
      //   chooseBoard();
      // }

      setStart(false);
    }
  };

  let status = winner
    ? `Winner: ${winner}`
    : `Next player: ${xIsNext ? 'X' : 'O'}`;
    console.log(board);
    console.log(bigboard);

  if (start){
    status = "Choose a Board";
  }

  return (
    <div>
      {startGame()}
      {renderBigBoard()}
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }

  return null;
}

function calculateBigWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }

  return null;
}

export default App;


