import React from 'react';
import './App.css';

function App() {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [xIsNext, setXIsNext] = useState(true);
  const winner = calculateWinner(board);

  const handleClick = (i) => {
    const squares = [...board];
    if (winner || squares[i]) return;

    squares[i] = xIsNext ? 'X' : 'O';
    setBoard(squares);
    setXIsNext(!xIsNext);
  };

  const renderSquare = (i) => (
    <button className="square" onClick={() => handleClick(i)}>
      {board[i]}
    </button>
  );

  // Define an array to represent the big Tic Tac Toe board
  const bigBoard = Array(3).fill(null).map(() =>
    Array(3).fill(null).map(() => Array(3).fill(null))
  );

  return (
    <div className="game">
      <div className="big-board">
        {bigBoard.map((row, rowIndex) => (
          <div key={rowIndex} className="big-board-row">
            {row.map((smallBoard, colIndex) => (
              <div key={colIndex} className="small-board">
                {smallBoard.map((square, squareIndex) => (
                  <div key={squareIndex} className="square">
                    {/* Render each square of the small Tic Tac Toe board */}
                    {/* You can add X or O here as needed */}
                    <div className="game">
                      <div className="game-board">
                        {/* <div className="status">{status}</div> */}
                        <div className="board">
                          {Array(3)
                            .fill(null)
                            .map((_, row) => (
                              <div key={row} className="board-row">
                                {Array(3)
                                  .fill(null)
                                  .map((_, col) => renderSquare(row * 3 + col))}
                              </div>
                            ))}
                        </div>
                        {/* <button className="replay-button" onClick={handleReplay}>
                          Replay
                        </button> */}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
