import React, { useState } from 'react';
import './App.css';

function App() {
  // const [board, setBoard] = useState(Array(9).fill(Array(9).fill(null))); //All connected 
  // const board = Array(9).fill(null).map(() => Array(9).fill(null)); // doesn't load in handleClick
  const [board, setBoard] = useState(Array(9).fill(null).map(() => Array(9).fill(null)));
  const [bigboard, setBigBoard] = useState(Array(9).fill(null));
  const [xIsNext, setXIsNext] = useState(true);
  const [start, setStart] = useState(true);
  const winner = calculateWinner(bigboard);
  const [playBoard, setPlayBoard] = useState(0);
  // board[8][8] = 'V';

  // const handleClick = (index, i) => {
  //   // const squares = [...board[index]];
  //   if (winner || board[index][i]) return;

  //   // squares[i] = xIsNext ? 'X' : 'O';
  //   board[index][i] = xIsNext ? 'X' : 'O';
  //   //setBoard(squares);
  //   setXIsNext(!xIsNext);
  // };

  const handleClick = (index, i) => {
    // Log the values of i and index
    console.log('i:', i, 'index:', index);

    // if (winner && board[index][i] != null) return;

    board[index][i] = xIsNext ? 'X' : 'O';
    console.log("Next: ", xIsNext ? 'X' : 'O');
    setXIsNext(!xIsNext);
  };

  const handleBigClick = (i) => {
    // needs to dim the other squares and only let the player play on the right square

  };

  const renderBigSquare = (i) => (
    <div className="board">
      {Array(3)
        .fill(null)
        .map((_, row) => (
          <div key={row} className="board-row">
            {Array(3)
              .fill(null)
              .map((_, col) => renderSquare(i, row * 3 + col))}
          </div>
        ))}
      </div>
  );

  const renderSquare = (index, i) => (
    <button className="square" onClick={() => handleClick(index, i)}>
      {board[index][i]}
    </button>
  );

  const handleReset = () => {
    // setBoard(Array(9).fill(null));
    //setBoard(Array(9).fill(Array(9).fill(null)));
    setBoard(Array(9).fill(null).map(() => Array(9).fill(null)));
    setXIsNext(true);
  };

  const renderBigBoard = () => (
    <div className="game">
      <div className="game-bigboard">
        <div className="status">{status}</div>
        <div> 
          {Array(3) 
            .fill(null)
            .map((_, row) => (
              <div key={row} className="board-row">
                {Array(3)
                  .fill(null)
                  .map((_, col) => renderBigSquare(row * 3 + col))}
              </div>
            ))}
            <button className="replay-button" onClick={handleReset}>
              Reset Board
            </button>
        </div>
      </div>
    </div>
  );

  const startGame = () =>{   
     if(start){

      setStart(false);
    }
  };

  const status = winner
    ? `Winner: ${winner}`
    : `Next player: ${xIsNext ? 'X' : 'O'}`;
    console.log(board);
    console.log(bigboard);



  return (
    <div>
      {startGame()}
      {renderBigBoard()}
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }

  return null;
}

export default App;
