      </div>
  );

  const renderSquare = (index, i) => (
    <button className="square" onClick={() => handleClick(index, i)}>
      {/* {board[index][i]} */}
      {board[i]}
    </button>
  );

  const handleReset = () => {
    setBoard(Array(9).fill(null));
    setXIsNext(true);
  };

  const renderBigBoard = () => {
    <div className="game">
    <div className="game-bigboard">
      <div className="status">{status}</div>
      <div className="board">
        {Array(3)
          .fill(null)
          .map((_, row) => (
            <div key={row} className="board-row">
              {Array(3)
                .fill(null)
                .map((_, col) => renderBigSquare(row * 3 + col))}
            </div>
          ))}
      </div>
    </div>
  </div>
  };

  const startGame = () =>{
    if(start){
      <div>

      </div>
      setStart(false);
    }
  }

  const status = winner
    ? `Winner: ${winner}`
    : `Next player: ${xIsNext ? 'X' : 'O'}`;
    console.log(board);


  return (
    <div className="game">
      {startGame}
    <div className="game-bigboard">
      <div className="status">{status}</div>
      <div> 
        {/* instead just manually do board[0][0] ... board[8][8] */}
        {Array(3) 
          .fill(null)
          .map((_, row) => (
            <div key={row} className="board-row">
              {Array(3)
                .fill(null)
                .map((_, col) => renderBigSquare(row, row * 3 + col))}
            </div>
          ))}
          <button className="replay-button" onClick={handleReset}>
          Reset Board
      </button>
      </div>
    </div>
  </div>

  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }

  return null;
}

export default App;
